
"use strict";

var bower = require('bower');     //bower组件安装
var defaultConfig = require('../config');
var cli = require('../util/cli');  //命令行

/**
 * 查看组件信息
 * iffront info moduleName
 * @moduleName 组件名称
 */
function info(moduleName) {
    bower.commands.info(moduleName , null , {'registry': defaultConfig.registry , "strictSsl":false}).on('end' , function(result){
           console.log(result);    
    });
}

// -------------------

info.line = function (argv) {
    var options = info.options(argv);
    var name = options.argv.remain[1];

    return info(name);
};

info.options = function (argv) {
    return cli.readOptions(argv);
};

info.completion = function () {
    // TODO:
};

module.exports = info;