/**
 * 安装依赖的npm库
 * iffront installnpm [<moduleName[#version]> [--save]]
 */

"use strict";

var Logger = require('../Logger');  //日志
var cli = require('../util/cli');  //命令行

function installnpm(options){
    var logger = new Logger();
     return logger;    
}
 

// ------------------- 接口方法

installnpm.line = function (argv) {
    var options = installnpm.options(argv);
    return installnpm(options);
};

installnpm.options = function (argv) {
    return cli.readOptions({}, argv);
};

installnpm.completion = function () {
    // TODO:
};

module.exports = installnpm;