
"use strict";

var shell = require('shelljs');
var prompt = require('../util/prompt');
var colors = require('../util/colors');

function  uploadcdn(options) {
}

// -------------------

uploadcdn.line = function(argv) {

    var options = [{
        message: '请输入您的AppName',
        name: 'userkey',
        require: true
    }];

    return prompt(options).then(function(result) {
        var userkey = result.userkey;
        if (shell.exec("grunt uploadcdn --userKey " + userkey ).code !== 0) {
            colors.red("Error: fpm uploadcdn failed");
        }
        process.exit(0);
    });
};




